import React from "react";
import PropTypes from "prop-types";
import '../../assets/styles/styles.css';
import { useState , useEffect} from "react";
import axios from "axios";
import { useLocation } from "react-router-dom";

export default function EditSocietyForm({ color }) {
    const location = useLocation();
    const socId = location.state.societyId;
    const url = "http://localhost:8000/society";
    const [socById, getSocietyByID] = useState([]);

    useEffect(()=>{
        const getSociety = ()=>{
            const editUrl = url+"/"+ socId;
            axios.get(editUrl)
            .then((response)=>{
                const societyDetail = response.data;
                getSocietyByID(societyDetail);
                // console.log(socById);
                // const s = Array.from(societyDetail)
                // console.log(s)
                // console.log("array length in function : "+ s.length+ "array is :"+ s)
            })
            .catch(error => console.error(`Error:${error}`));
        }
        getSociety();
    },[socId]);

    

    // const s = Array.from(socById);
    console.log("yea hai : ");
    console.log(socById);
    // const result = Object.values(socById)
    // console.log(Array.from(socById));
    // var convert  = Object.keys(socById).map(function(key)  
    // {  
    //     return [Number(key), socById[key]];  
    // });  
    const arr = socById.map((soc, index)=>{
        return(
            <tbody>
                <tr>
                    <th className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4 text-left flex items-center">
                    {index +1 }
                    </th>
                    <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                    {soc.name}
                    </td>
                    <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                    {soc.address}
                    </td>
                    <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                    {soc.adminName}
                    </td>
                    <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                    {soc.contact }
                    </td>
                    <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                    <a href="/admin/societyMembersList">View members</a>
                    </td>
                    <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                    <button className="px-3 py-2" value={soc._id} onClick={e=>editSociety(e)}>Edit</button>
                    </td>
                    <td className="border-t-0 px-6 align-middle border-l-0 border-r-0 text-xs whitespace-nowrap p-4">
                    <button className="px-3 py-2" value={soc._id} onClick={e=>deleteSociety(e)}>Delete</button>
                    </td>
                </tr>
                </tbody>
        )
    
    })  

    const [society ,setSociety] = useState({ 
        name:"", 
        address:"",
        adminName:"",
        contact:""
    })

    function submit(e){
        e.preventDefault();
        axios.put(url+"/"+socId,{
        name:society.name,
        address:society.address,
        adminName:society.adminName,
        contact:society.contact
        })
        .then(res=>{
        console.log(res.data)
        })
    }

    function handle(e){
        const newData = {...society}
        newData[e.target.name]=e.target.value
        setSociety(newData)
        console.log(newData)
    }

    
    return (
    <>
    <div
            className={
            "relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded " +
            (color === "light" ? "bg-white" : "bg-lightBlue-900 text-white")
            }
        >
            <div className="rounded-t mb-0 px-4 py-3 border-0">
                <div className="flex flex-wrap items-center">
                    <div className="relative w-full px-4 max-w-full flex-grow flex-1">
                    <h3
                        className={
                        "font-semibold text-lg " +
                        (color === "light" ? "text-blueGray-700" : "text-white")
                        }
                    >
                    Edit Society
                    </h3>
                    </div>
                </div>
            </div>
            <form onSubmit={(e)=>submit(e)}>
            <div className="container bg-lightBlue-900 text-white p-4">
                <div className="row py-4 px-2">
                    <div className="col-2">
                        <label>Society Name :</label>
                    </div>
                    <div className="col-10">
                        <input type="text" name="name" value={society.name} onChange={(e)=>handle(e)} className="form-control" placeholder="Society name..."></input>
                    </div>
                </div>
                <div className="row py-4 px-2">
                    <div className="col-2">
                        <label>Society Address :</label>
                    </div>
                    <div className="col-10">
                        <textarea type="text" name="address" value={society.address} onChange={(e)=>handle(e)}className="form-control" placeholder="Address"></textarea>
                    </div>
                </div>
                <div className="row py-4 px-2">
                    <div className="col-2">
                        <label>Name :</label>
                    </div>
                    <div className="col-4">
                        <input type="text" name="adminName" value={society.adminName} onChange={(e)=>handle(e)}className="form-control" placeholder="Client name"></input>
                    </div>
                    <div className="col-2">
                        <label>Contact number :  </label>
                    </div>
                    <div className="col-4">
                        <input name="contact" value={society.contact} onChange={(e)=>handle(e)} className="form-control" placeholder="Phone number"></input>
                    </div>

                </div>
                <div className="row py-4 px-2 text-center">
                    <div className="col-5"></div>
                    <button className="col-2 form-control">Submit </button>
                    <div className="col-5"></div>
                </div>
            </div>
            </form>

            {/* YAHA THA FORM */}
    </div>
        
    </>
    );
    
}

EditSocietyForm.defaultProps = {
    color: "light",
};

EditSocietyForm.propTypes = {
    color: PropTypes.oneOf(["light", "dark"]),
};
